##### gulpfile.js

	var gulp=require('gulp');
	var concat = require('gulp-concat');//
	var uglify = require('gulp-uglify');//
	var jshint = require('gulp-jshint');//jshint
	var cleanCSS = require('gulp-clean-css');// CSS 
	var htmlmin = require('gulp-htmlmin');// html 
	var imagemin = require('gulp-imagemin');//
	var rename = require('gulp-rename');//
	var del = require('del');//
	var rev = require('gulp-rev');//- MD5
	var revCollector = require('gulp-rev-collector');//- 
	var babel = require('gulp-babel');//ES6ES2015
	var less = require('gulp-less');//less
	var plumber = require('gulp-plumber');//
	var notify = require('gulp-notify');
	var autoprefixer = require('gulp-autoprefixer');//css3
	var px2rem = require('gulp-px3rem');//pxrem

	//
	gulp.task('clean',function(cb){
		del(['./dist/**/*','./rev/**/*'],cb);
	});
	gulp.task('cleanCss',function(cb){
		del(['./dist/css/*'],cb);
	});
	//js
	gulp.task('js',function(){
		return gulp.src('./js/*.js')
			.pipe(jshint())
			.pipe(uglify())
			.pipe(concat('all'))
			.pipe(rename(function(path){
				path.basename +='.min';
				path.extname ='.js'
			}))
			.pipe(rev())
			.pipe(gulp.dest('./dist/js/'))
			.pipe(rev.manifest())
			.pipe(gulp.dest('./rev/js'));
	});
	//less
	gulp.task('less2css',function(){
		return gulp.src('./less/common.less')
			.pipe(plumber({errorHandler: notify.onError('Error: <%= error.message %>')}))  //
			.pipe(less())
			.pipe(gulp.dest('./css/'))
	});
	//css
	gulp.task('css',['less2css'],function(){
		return gulp.src('./css/*.css')
			.pipe(cleanCSS())
			.pipe(concat('all'))
			.pipe(rename(function(path){
				path.basename +='.min';
				path.extname ='.css'
			}))
			.pipe(rev())
			.pipe(gulp.dest('./dist/css/'))
			.pipe(rev.manifest())
			.pipe(gulp.dest('./rev/css'));
	});
	//css3
	gulp.task('autoFx',['css'],function(){
	   return gulp.src('./dist/css/*.css')
			.pipe(autoprefixer({
				browsers: ['last 5 versions']
			}))
			.pipe(gulp.dest('./dist/css'));
	});
	//css px2rem
	gulp.task('px2rem', function() {
		return gulp.src('./dist/css/*.css')
			.pipe(px2rem())
			.pipe(gulp.dest('./dist/css'))
	});
	//html
	gulp.task('html', function() {
		var options = {
			removeComments: true,  //HTML
			collapseWhitespace: true,  //HTML
			collapseBooleanAttributes: true,  // <input checked="true"/> ==> <input checked />
			removeEmptyAttributes: true,  // <input id="" /> ==> <input />
			removeScriptTypeAttributes: true,  //<script>type="text/javascript"
			removeStyleLinkTypeAttributes: true,  //<style><link>type="text/css"
			minifyJS: true,  //JS
			minifyCSS: true  //CSS
		};
		return gulp.src('./*.html')
			.pipe(htmlmin(options))
			.pipe(gulp.dest('./dist/'));
	});
	//
	gulp.task('img', function(){
		gulp.src('./images/*')
			.pipe(imagemin({
				optimizationLevel: 5, //Number  3  0-7
				progressive: true, //Boolean false jpg
				interlaced: true, //Boolean false gif
				multipass: true //Boolean false svg
			}))
			.pipe(gulp.dest('dist/images'));
	});

	//
	gulp.task('rev',['autoFx','js'],function(){
		return gulp.src(['./rev/**/*.json','./dist/*.html'])
			.pipe(revCollector({replaceReved: true}))
			.pipe(gulp.dest('./dist/'));
	});

	//less
	gulp.task('lessWatch', function () {
		gulp.watch('./less/*.less', ['cleanCss','rev']);
	});

	gulp.task('tigger',['css','js','img','html']);


